css:
.print-heading {
    text-align: center;
    margin: 0 auto;
    max-width: 800px;
    display: flex;
    align-items: center;
    justify-content: space-between;
}
.print-heading img {
    height: 90px;
}
.text-container {
    text-align: center;
    flex-grow: 1;
    margin-left: 10px; 
}

body {
    font-family: Arial, sans-serif; 
    font-size: 14px;
    line-height: 1.6;
    color: #333;
}

.section-break {
    margin-top: 10px;
    margin-bottom: 10px;
}

.column-break {
    column-break-inside: avoid;
    page-break-inside: avoid;
}

label {
    font-weight: bold;
}

.value {
    font-style: italic;
}

.bold-row {
font-weight: bold; 
margin-top: 15px;
margin-bottom: 5px;
text-decoration: underline;
}
.box {
border: 1px solid #000;
padding: 2px;
padding-top: 2px;
margin: 2px;
}
