css:
      .print-heading {
            text-align: center;
            margin: 0 auto;
            max-width: 800px;
            display: flex;
            align-items: center;
            justify-content: space-between;
        }
        .print-heading img {
            height: 80px;
        }
        .text-container {
            text-align: center;
            flex-grow: 1;
            margin-left: 10px; /* Adjust the margin as needed */
        }

   body {
            font-family: Arial, sans-serif; /* Set your preferred font */
            font-size: 14px;
            line-height: 1.6;
            color: #333;
        }

        .section-break {
            margin-top: 20px;
            margin-bottom: 20px;
        }

        .column-break {
            column-break-inside: avoid;
            page-break-inside: avoid;
        }

        label {
            font-weight: bold;
        }

        .value {
            font-style: italic;
        }

        hr {
            border-top: 0.5px solid #ccc;
            margin-top: 10px;
            margin-bottom: 10px;
        }

    /* Common table class */
.custom-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 5px;
}

/* Payment Schedule Table styles */
.custom-table th,
.custom-table td {
    text-align: center;
    width: auto;
    page-break-inside: avoid; 
    font-family: 'Arial', sans-serif; 
    font-size: 12px; 
    font-weight: normal; 
    border: 1px solid #ccc; 
    padding: 8px; 
}

.custom-table th {
    font-weight: bold; 
}

.heading_row {
    margin-top: 10px;
    margin-bottom: 10px;
}
